import React, { useState } from "react";
import { useMoralisWeb3Api } from "react-moralis";
import { Card, Image, Tooltip, Modal, Alert, Spin } from "antd"; // Input, Button
import { useNFTBalance } from "hooks/useNFTBalance";
import { FileSearchOutlined, DownloadOutlined } from "@ant-design/icons";
import { useMoralisDapp } from "providers/MoralisDappProvider/MoralisDappProvider";
import { getExplorer } from "helpers/networks";
// import { useWeb3ExecuteFunction } from "react-moralis";

const { Meta } = Card;

const styles = {
  NFTs: {
    display: "flex",
    flexWrap: "wrap",
    WebkitBoxPack: "start",
    justifyContent: "flex-start",
    margin: "0 auto",
    maxWidth: "1000px",
    gap: "10px",
  },
};

function NFTBalance() {
  const { NFTBalance, fetchSuccess } = useNFTBalance();
  const { chainId } = useMoralisDapp();
  const [visible, setVisibility] = useState(false);
  const [nftToGrabFiles, setNftToGrabFiles] = useState(null);
  const Web3Api = useMoralisWeb3Api();
  // const downloadProcessor = useWeb3ExecuteFunction();
  const [loading, setLoading] = useState(false);

  async function fetchNFTMetadata(nft) {
    setLoading(true);
    const options = {
      chain: chainId,
      address: nft.token_address,
      token_id: nft.token_id
    };
    const metaData = await Web3Api.token.getTokenIdMetadata(options)
    const metadata = JSON.parse(metaData.metadata);    
    const attributes = metadata.attributes

    for (let i = 0; i < attributes.length; i++) {
      try {
        let hash = '';
        let name = '';
        name = attributes[i].value
        hash = attributes[i].file
        let ipfsHash = hash.replace("ipfs://", "")
        const url = `https://ipfs.moralis.io:2053/ipfs/${ipfsHash}`
        const response = await fetch(url).then(r => r.blob())
        var fileDownload = require('js-file-download');
        fileDownload(response, name + ".stl")
      } catch (e) {
        break;
      }
    }

    await fetch({
      params: options,
      onSuccess: () => {
        console.log('success');
        setLoading(false);
        setVisibility(false);
        succDownload();
      },
      onError: (error) => {
        console.log('fail')
        setLoading(false);
        failDownload();
      }
    })
  }

  function succDownload() {
    let secondsToGo = 5;
    const modal = Modal.success({
      title: "Success!",
      content: `Files succesfully downloaded`,
    });
    setTimeout(() => {
      modal.destroy();
    }, secondsToGo * 1000);
  }

  function failDownload() {
    let secondsToGo = 5;
    const modal = Modal.error({
      title: "Error!",
      content: `No files found!`,
    });
    setTimeout(() => {
      modal.destroy();
    }, secondsToGo * 1000);
  }

  const handleDownloadFileClick = (nft) => {
    setNftToGrabFiles(nft);
    setVisibility(true);
  }

  return (
    <>
      <div style={styles.NFTs}>
        {!fetchSuccess && (
          <>
            <Alert
              message="Unable to fetch all NFT metadata... We are searching for a solution, please try again later!"
              type="warning"
            />
            <div style={{ marginBottom: "10px" }}></div>
          </>
        )} 
        {NFTBalance &&
          NFTBalance.map((nft, index) => (
            <Card
              hoverable
              actions={[
                <Tooltip title="View Token on Block Explorer">
                  <FileSearchOutlined
                    onClick={() =>
                      window.open(
                        `${getExplorer(chainId)}token/${nft.token_address}`,
                        "_blank"
                      )
                    }
                  />
                </Tooltip>,
                <Tooltip title="Download Files, if available">
                  <DownloadOutlined onClick={() => handleDownloadFileClick(nft)} />
                </Tooltip>,
              ]}
              style={{ width: 240, border: "2px solid #e7eaf3" }}
              cover={
                <Image
                  preview={false}
                  src={nft?.image || "error"}
                  fallback="data:image/png;base64,"
                  alt=""
                  style={{ height: "240px" }}
                />
              }
              key={index}
            >
              <Meta title={nft.name} description={nft.contract_type}/>
            </Card>
          ))}
      </div>
        <Modal
          title={`Download ${nftToGrabFiles?.name} #${nftToGrabFiles?.token_id} Files`}
          visible={visible}
          okText="Download Files"
          onOk={() => fetchNFTMetadata(nftToGrabFiles)}
          onCancel={() => setVisibility(false)}
        >
          <p>Click Download Files to download the 3d printer files, if available!</p>
            <Spin spinning={loading}>
            <img alt="NFT"
              src={`${nftToGrabFiles?.image}`}
              style={{
                width: "250px",
                margin: "auto",
                borderRadius: "10px",
                marginBottom: "15px",
              }}
            />
            </Spin>
        </Modal>
    </>
  );
}

export default NFTBalance;
