import React, { useState } from "react";
import { useMoralisWeb3Api } from "react-moralis";
import { Card, Image, Tooltip, Modal, Input, Alert, Spin, Button } from "antd";
import { useNFTBalance } from "hooks/useNFTBalance";
import { FileSearchOutlined, DownloadOutlined } from "@ant-design/icons";
import { useMoralisDapp } from "providers/MoralisDappProvider/MoralisDappProvider";
import { getExplorer } from "helpers/networks";

const { Meta } = Card;

const styles = {
  NFTs: {
    display: "flex",
    flexWrap: "wrap",
    WebkitBoxPack: "start",
    justifyContent: "flex-start",
    margin: "0 auto",
    maxWidth: "1000px",
    gap: "10px",
  },
};

function NFTBalance() {
  const { NFTBalance, fetchSuccess } = useNFTBalance();
  const { chainId } = useMoralisDapp();
  const [visible, setVisibility] = useState(false);
  const [nftToGrabFiles, setNftToGrabFiles] = useState(null);
  const Web3Api = useMoralisWeb3Api();

  async function fetchNFTMetadata(nft) {
    const options = {
      chain: chainId,
      address: nft.token_address,
      token_id: nft.token_id
    };
    const metaData = await Web3Api.token.getTokenIdMetadata(options)
    const metadata = JSON.parse(metaData.metadata);
    const attributes = metadata.attributes
    // console.log(metadata)

    let files;
    for (let i = 0; i < attributes.length; i++) {
      require("downloadjs")
      let hash = '';
      hash = attributes[i].file
      let ipfsHash = hash.replace("ipfs://", "")
      const url = `https://ipfs.moralis.io:2053/ipfs/${ipfsHash}`
      const response = await fetch(url)
      download(response, test)
      // console.log(response)
      files.push(response)

      // async function fetchIPFSFile(hash) {
      //   const url = hash;
      //   const response = await fetch(url);
      //   return await response.json();
      // }
    }

    console.log(files)

  }

  const handleDownloadFileClick = (nft) => {
    setNftToGrabFiles(nft);
    setVisibility(true);
  }

  return (
    <>
      <div style={styles.NFTs}>
        {/* {contractABIJson.noContractDeployed && (
          <>
            <Alert
              message="No Smart Contract Details Provided. Please deploy smart contract and provide address + ABI in the MoralisDappProvider.js file"
              type="error"
            />
            <div style={{ marginBottom: "10px" }}></div>
          </>
        )}
        {!fetchSuccess && (
          <>
            <Alert
              message="Unable to fetch all NFT metadata... We are searching for a solution, please try again later!"
              type="warning"
            />
            <div style={{ marginBottom: "10px" }}></div>
          </>
        )} */}
        {NFTBalance &&
          NFTBalance.map((nft, index) => (
            <Card
              hoverable
              actions={[
                <Tooltip title="View On Blockexplorer">
                  <FileSearchOutlined
                    onClick={() =>
                      window.open(
                        `${getExplorer(chainId)}token/${nft.token_address}?a=${nft.token_id}#inventory`,
                        "_blank"
                      )
                    }
                  />
                </Tooltip>,
                <Tooltip title="Download Files, if available">
                  <DownloadOutlined onClick={() => handleDownloadFileClick(nft)} />
                </Tooltip>,
              ]}
              style={{ width: 240, border: "2px solid #e7eaf3" }}
              cover={
                <Image
                  preview={false}
                  src={nft?.image || "error"}
                  fallback="data:image/png;base64,"
                  alt=""
                  style={{ height: "240px" }}
                />
              }
              key={index}
            >
              <Meta title={nft.name} description={nft.contract_type}/>
            </Card>
          ))}
      </div>
        <Modal
          title={`Download ${nftToGrabFiles?.name} #${nftToGrabFiles?.token_id} Files`}
          visible={visible}
          onOk={() => fetchNFTMetadata(nftToGrabFiles)}
          onCancel={() => setVisibility(false)}
        >
          <p>Click OK to download the 3d printer files!</p>
        </Modal>
    </>
  );
}

export default NFTBalance;
