import React, { useState } from "react";
import "./styles.css";

const NFTMint = ({ onAdd }) => {

  const [name, setName] = useState('')
  const [creator, setCreator] = useState('')
  const [category, setCategory] = useState('')
  const [supply, setSupply] = useState('')
  const [description, setDescription] = useState('')
  const [image, setImage] = useState('')
  const [file, setFile] = useState('')

  const onSubmit = (e) => {
    e.preventDefault()

    if(!name) {
        alert('Missing name!!!')
        return
    }

    if(!creator) {
        alert('Missing creator!!!')
        return
    }

    if(category === '') {
        alert('Choose Category!!!')
        return
    }

    if(!supply) {
        alert('Missing supply!!!')
        return
    }

    if(!description) {
        alert('Missing description!!!')
        return
    }

    if(!image) {
        alert('Missing image!!!')
        return
    }

    if(!file) {
        alert('Missing file!!!')
        return
    }

    onAdd({ name, creator, category, supply, description, image, file })

    setName('')
    setCreator('')
    setCategory('')
    setSupply('')
    setDescription('')
    setImage('')
    setFile('')
  }

  function preview() {
    if (image==null) {
      return <div>
        <div className="preview-box" style={{paddingTop: "25%"}}>
            <span style={{color: "whitesmoke"}}>Upload a file to see a preview</span>
        </div>
      </div>
    }
    return <div>
      {image && (
          <div className="preview-box" style={{paddingTop: "1%"}}>
            <img src={image} alt="preview" width="95%" hieght="95%" />
          </div>
      )}
    </div>
  }

  return (
    <section>
    <div className="col-12">
      <div className="title" style={{fontFamily: 'Roboto, sans-serif', fontSize: "2rem", marginTop: "0rem", marginBottom: '3rem', color: "whitesmoke"}}>Lazy Mint on Rarible </div>
        <div className="row">
          <div className="col-5 offset-2">
            <div id="success_message"></div>
				      <div className="form_element">
					      <div className="col-md-3" style={{marginLeft: "-25px"}}>
					    	  <label className="form-label" style={{fontSize: "1.25rem", color: "whitesmoke"}}>Upload Image<span className="text-danger">*</span></label>
					      </div>
                  <div className="upload-box">
                    <input
                      id="input_image"
                      type="file"
                      name="image" 
                      accept="image/png, image/jpeg"
                      style={{paddingTop: "15%"}} 
                      placeholder=""
                      onChange={e => {
                        const imgUrl = URL.createObjectURL(e.target.files[0]);
                        console.log(imgUrl);
                        setImage(imgUrl);
                        }}
                      />
                    <p style={{fontSize: "1rem", color: "whitesmoke"}}>Click or drag a image to this area to upload</p>
                  </div>
                </div>
              <div className="form_element">
                <div className="col-md-2">
                  <label className="form-label" style={{fontSize: "1.25rem", marginLeft: "-10px", color: "whitesmoke"}}>Name<span className="text-danger">*</span></label>
                </div>
                <input className="inp" style={{color: "whitesmoke"}} type=" text" id="input_name" name="name" placeholder="Insert name of the file"></input>
              </div>
              <div className="form_element">
                <div className="col-md-1">
                  <label className="form-label" style={{fontSize: "1.25rem", color: "whitesmoke"}}>Supply<span className="text-danger">*</span></label>
                </div>
                <input className="inp" style={{color: "whitesmoke"}} type=" text" id="input_quantity" name="Quantity" placeholder="Enter quantity of NFTs to mint per file"/>
              </div>
              <div className="form_element">
                <div className="col-md-2" style={{marginLeft: "-15px", marginBottom: "5px"}}>
                  <label className="form-label" style={{fontSize: "1.25rem", color: "whitesmoke"}}>Category <span className="text-danger">*</span></label>
                </div>
                <select className="inp" style={{color: "whitesmoke"}} type=" text" id="input_tag" name="Tag" placeholder="Tag">
                  <option value="Figurines">Figurines</option>
                  <option value="Automotive">Automotive</option>
                  <option value="Sports">Sports</option>
                  <option value="Other">Other</option>
                </select>
              </div>
              <div className="form_element">
                <div className="col-md-1">
                  <label className="form-label" style={{fontSize: "1.25rem", color: "whitesmoke"}}>Creator<span className="text-danger">*</span></label>
                </div>
                <input className="inp" style={{color: "whitesmoke"}} type=" text" id="input_creator" name="name" placeholder="Insert creator name"/>
              </div>
              <div className="form_element">
                <div className="col-md-1" style={{marginLeft: "-15px", color: "whitesmoke"}}>
                  <label className="form-label" style={{fontSize: "1.25rem", scrollBehavior: "auto"}}>Description <span style={{color: "gray"}}>(optional)</span></label>
                </div>
                <input className="inp" style={{color: "whitesmoke"}} type=" text" id="input_description" name="description" placeholder="Description of the file"/>
              </div>
            </div>

            <div className="col-5 offset-7" style={{position: "fixed", height: "40vh", marginTop: "1rem", marginBottom: "5rem"}}>
                <div className="title" style={{fontFamily: "Roboto, sans-serif", fontSize: "1.5rem", float: "left", marginLeft: "2rem", marginBottom: "1.5rem", color: "whitesmoke"}}>Image Preview</div>
                {preview()}
                </div>      
              </div>
            </div>
            <div className="row col-5 offset-4" style={{marginTop: "5rem"}}>
              <button type="button" className="btn btn-primary btn-lg" htmlType="submit">Submit</button>
            </div> 
  </section>
  )
}

export default NFTMint;
